import React from 'react';

const Card = ({ details }) => {
  return (
    <div className='card'>
      <div className='image'>
        <img src={require(`../../img/${details.image}`)} alt={details.nom} />
      </div>
      <div className='recette'>
        <h2>{details.nom}</h2>
      </div>
    </div>
  )
}

export default Card;
